INSERT INTO `introduction` (`id`, `title`, `info`, `danhmuc`, `active`) VALUES
(1, ' Trả Góp FPT  - Một cách tiêu dùng thông minh !', '<p>\r\n\n	Ch&agrave;o mừng bạn đến với Trả G&oacute;p FPT -&nbsp;Hệ thống Trung t&acirc;m Trả g&oacute;p thuộc C&ocirc;ng ty TNHH B&aacute;n lẻ FPT.</p>\r\n\n<p>\r\n\n	Với t&acirc;m huyết x&acirc;y dựng một Hệ thống bao gồm những Trung t&acirc;m B&aacute;n lẻ c&oacute; kh&ocirc;ng gian mua sắm tiện nghi, hiện đại, mang đến những sản phẩm ch&iacute;nh h&atilde;ng với gi&aacute; th&agrave;nh cạnh tranh đến tận tay người ti&ecirc;u d&ugrave;ng, Thương hiệu FPT Shop đ&atilde; ra đời v&agrave;o th&aacute;ng 8/2007 với nhiều shop tại H&agrave; Nội , Tp.HCM v&agrave; tr&ecirc;n To&agrave;n Quốc.</p>\r\n\n<p>\r\n\n	&nbsp;</p>\r\n\n<p>\r\n\n	Trong nỗ lực ph&aacute;t triển lớn mạnh, TragopFPT.Com ra đời nhằm đ&aacute;p ứng sự tiện nghi về phong c&aacute;ch mua sắm,th&ocirc;ng minh về c&aacute;ch ti&ecirc;u d&ugrave;ng bằng h&igrave;nh thức Trả G&oacute;p với phong c&aacute;ch v&agrave; ưu thế vượt trội về h&igrave;nh thức mua sắm tin cậy khi sản phẩm được cung cấp bởi hệ thống trung t&acirc;m b&aacute;n lẻ thuộc c&ocirc;ng ty TNHH b&aacute;n lẻ FPT &ndash; Thương hiệu uy tin đ&atilde; được khẳng định tr&ecirc;n thị trường.</p>\r\n\n<p>\r\n\n	Ch&uacute;ng t&ocirc;i cung cấp dịch vụ trả g&oacute;p với tất cả c&aacute;c sản phẩm tr&ecirc;n : <a href="http://www.fptshop.com.vn/">www.fptshop.com.vn</a> , <a href="http://www.vimua.com/">www.vimua.com</a> &amp; <a href="http://www.tragopfpt.com/">www.tragopfpt.com</a>.</p>\r\n\n<p>\r\n\n	&nbsp;</p>\r\n\n<p>\r\n\n	Ngo&agrave;i ra TragopFPT.com c&ograve;n cung cấp dịch vụ b&aacute;n h&agrave;ng trực tuyến. Qu&yacute; kh&aacute;ch sẽ &nbsp;kh&ocirc;ng phải tốn thời gian v&agrave; tiền bạc m&agrave; chỉ cần thoải m&aacute;i lựa chọn tại nh&agrave; hoặc văn ph&ograve;ng l&agrave;m việc m&agrave; vẫn mua được sản phẩm ch&iacute;nh h&atilde;ng với mức gi&aacute; hợp l&yacute; nhất thị trường.</p>\r\n\n<p>\r\n\n	&nbsp;</p>\r\n\n<p>\r\n\n	Đến với TragopFPT.Com, kh&aacute;ch h&agrave;ng sẽ c&oacute; được những sản phẩm ưng &yacute; với &nbsp;ch&iacute;nh s&aacute;ch ưu đ&atilde;i, thủ tục đơn giản, nhanh gọn &hellip; Đội ngũ nh&acirc;n vi&ecirc;n tư vấn b&aacute;n h&agrave;ng trẻ trung, nhiệt t&igrave;nh, hiểu biết v&agrave; hướng dẫn chu đ&aacute;o tận t&igrave;nh. Đồng thời với những đối t&aacute;c chiến lược l&agrave; nhiều tập đo&agrave;n t&agrave;i ch&iacute;nh lớn mạnh như PPF, ACS, SGVF. TragopFPT.Com mang đến dịch vụ trả g&oacute;p nhanh nhất, uy t&iacute;n v&agrave; đang tin cậy nhất.</p>\r\n\n<p>\r\n\n	&nbsp;</p>\r\n\n<p>\r\n\n	Hệ thống B&aacute;n lẻ FPT Shop lu&ocirc;n kh&ocirc;ng ngừng cải tiến chất lượng sản phẩm, dịch vụ với mục ti&ecirc;u v&agrave; cam kết l&acirc;u d&agrave;i &ldquo;nỗ lực đem đến cho kh&aacute;ch h&agrave;ng những sản phẩm tốt nhất c&ugrave;ng dịch vụ ho&agrave;n hảo nhất&rdquo;.</p>\r\n\n<p>\r\n\n	&nbsp;</p>\r\n\n<p>\r\n\n	Th&ocirc;ng tin :</p>\r\n\n<p>\r\n\n	C&ocirc;ng ty TNHH B&aacute;n lẻ FPT :</p>\r\n\n<p>\r\n\n	( Ngay sau khi đặt h&agrave;ng - Qu&yacute; kh&aacute;ch sẽ được trực tiếp FPT li&ecirc;n hệ, hỗ trợ v&agrave; giao h&agrave;ng )</p>\r\n\n<p>\r\n\n	Cung cấp c&aacute;c sản phẩm điện tử viễn th&ocirc;ng ch&iacute;nh h&atilde;ng do FPT nhập khẩu hoặc l&agrave;m đại l&yacute; ph&acirc;n phối ch&iacute;nh thức tại thị trường Việt Nam</p>\r\n\n<p>\r\n\n	Trụ sở ch&iacute;nh: 24T2 Trung H&ograve;a Nh&acirc;n Ch&iacute;nh - Cầu Giấy - H&agrave; Nội</p>\r\n\n<p>\r\n\n	Điện thoại: (04) 35.37 8899</p>\r\n\n<p>\r\n\n	Fax: (04) 35.77 1565</p>\r\n\n<p>\r\n\n	PHƯƠNG THỨC THANH TO&Aacute;N :</p>\r\n\n<p>\r\n\n	Tại H&agrave; Nội v&agrave; Hồ Ch&iacute;&nbsp; Minh : Nhận h&agrave;ng v&agrave; thanh to&aacute;n cho nh&acirc;n vi&ecirc;n giao h&agrave;ng</p>\r\n\n<p>\r\n\n	Tại Hải Ph&ograve;ng, Nghệ An, Đ&agrave; Nẵng, Thanh h&oacute;a : .Vui l&ograve;ng đặt h&agrave;ng online - Gi&aacute; ưu đ&atilde;i v&agrave; qua kho FPT lấy h&agrave;ng.</p>\r\n\n<p>\r\n\n	C&aacute;c tỉnh kh&aacute;c : Qu&yacute; kh&aacute;ch h&agrave;ng vui l&ograve;ng chuyển khoản trước v&agrave;o t&agrave;i khoản của C&ocirc;ng ty B&aacute;n lẻ FPT.</p>\r\n\n<p>\r\n\n	Th&ocirc;ng tin t&agrave;i khoản:</p>\r\n\n<p>\r\n\n	Chủ t&agrave;i khoản: C&ocirc;ng ty TNHH B&aacute;n lẻ FPT</p>\r\n\n<p>\r\n\n	Số TK: 05680568004</p>\r\n\n<p>\r\n\n	Ng&acirc;n h&agrave;ng: Ng&acirc;n h&agrave;ng Thương mại cổ phần Ti&ecirc;n Phong chi nh&aacute;nh H&agrave; Nội</p>\r\n\n<p>\r\n\n	Chi nh&aacute;nh Hồ Ch&iacute; Minh&nbsp;</p>\r\n\n<p>\r\n\n	Chủ t&agrave;i khoản: Chi nh&aacute;nh Cty TNHH B&aacute;n lẻ FPT (TP.HN)</p>\r\n\n<p>\r\n\n	Số t&agrave;i khoản: 201 7777 9999 001&nbsp;</p>\r\n\n<p>\r\n\n	Tại: Ng&acirc;n h&agrave;ng Ti&ecirc;n Phong Bank (TP.HCM) -&nbsp;Chi nh&aacute;nh Nguyễn Đ&igrave;nh Chiểu Q.3 Tp.HCM&nbsp; &nbsp; &nbsp;</p>\r\n\n<p>\r\n\n	Trong thời gian tới ch&uacute;ng t&ocirc;i sẽ ho&agrave;n thiện giải ph&aacute;p thanh to&aacute;n trả trước&nbsp;SMS Banking, Internet Banking của c&aacute;c ng&acirc;n h&agrave;ng: Tien Phong Bank, Vietcombank,DongA Bank, Techcombank</p>\r\n\n', 1, 1),
(2, 'Công ty TNHH Bán Lẻ FPT', '<p>\r\n\n	&nbsp;Giới thiệu dịch vụ &quot;mua h&agrave;ng trả g&oacute;p&quot;</p>\r\n\n<ul>\r\n\n	<li>\r\n\n		Điện thoại iPhone s&agrave;nh điệu, Nokia thời trang,... bạn muốn sở hữu một chiếc ngay h&ocirc;m nay.</li>\r\n\n	<li>\r\n\n		Bạn muốn c&oacute; một chiếc m&aacute;y t&iacute;nh x&aacute;ch tay phục vụ cho c&ocirc;ng việc v&agrave; học tập.</li>\r\n\n</ul>\r\n\n<p>\r\n\n	Bạn đang lo ngại v&igrave; gi&aacute; trị lớn của sản phẩm hoặc bạn muốn mua nhiều sản phẩm một l&uacute;c nhưng chưa đủ khả năng t&agrave;i ch&iacute;nh.</p>\r\n\n<p>\r\n\n	Nhằm hỗ trợ kh&aacute;ch h&agrave;ng tiếp cận tới những sản phẩm mơ ước ngay cả khi vấn đề t&agrave;i ch&iacute;nh l&agrave; một chướng ngại, chương tr&igrave;nh &quot;Mua h&agrave;ng trả g&oacute;p&quot; của trung t&acirc;m b&aacute;n lẻ trực tuyến TragopFPT.Com sẽ gi&uacute;p qu&yacute; kh&aacute;ch c&oacute; được những sản phẩm ưng &yacute; chỉ với một số tiền tiết kiệm nhỏ h&agrave;ng th&aacute;ng.</p>\r\n\n<p>\r\n\n	Chương tr&igrave;nh &quot;Mua h&agrave;ng trả g&oacute;p&quot; của TragopFPT.Com &aacute;p dụng cho mọi kh&aacute;ch h&agrave;ng với thủ tục đơn giản kh&ocirc;ng cần thế chấp nhằm kh&aacute;ch h&agrave;ng c&oacute; thể mua được c&aacute;c sản phẩm cần thiết lại c&oacute; thể c&oacute; thể chủ động về nguồn t&agrave;i ch&iacute;nh trong việc thanh to&aacute;n.</p>\r\n\n<p>\r\n\n	Chương tr&igrave;nh &quot;Mua h&agrave;ng trả g&oacute;p&quot; &aacute;p dụng cho c&aacute;c sản phẩm &quot;m&aacute;y t&iacute;nh, m&aacute;y ảnh, điện thoại&quot;</p>\r\n\n<p>\r\n\n	Khi tham gia v&agrave;o chương tr&igrave;nh &quot;Mua h&agrave;ng trả g&oacute;p&quot; qu&yacute; kh&aacute;ch chỉ cần trả trước từ 30% đến 70% gi&aacute; trị của sản phẩm v&agrave; tiết kiệm một khoản nhỏ h&agrave;ng ng&agrave;y l&agrave; c&oacute; thể sở hữu ngay sản phẩm mơ ước.</p>\r\n\n<p>\r\n\n	Để hiểu r&otilde; hơn về c&aacute;ch thức v&agrave; quy tr&igrave;nh mua h&agrave;ng trả g&oacute;p TragopFPT.Com xin tr&acirc;n trọng giới thiệu tới qu&yacute; kh&aacute;ch c&aacute;c đối t&aacute;c quản l&yacute; dịch vụ &quot;Mua h&agrave;ng trả g&oacute;p&quot; của Trung t&acirc;m b&aacute;n lẻ trực tuyến TragopFPT.Com. Qu&yacute; kh&aacute;ch c&oacute; thể lựa chọn bất cứ đối t&aacute;c n&agrave;o của TragopFPT.Com để tham gia chương tr&igrave;nh.</p>\r\n\n<ul>\r\n\n	<li>\r\n\n		<a href="http://tragopfpt.com/vi/html/id16/Dich-vu-tra-gop-SGVF/">C&ocirc;ng ty SG Việt Finance</a></li>\r\n\n	<li>\r\n\n		<a href="http://tragopfpt.com/vi/html/id15/Dich-vu-tra-gop-cua-doi-tac-Home-Credit/">C&ocirc;ng ty t&agrave;i ch&iacute;nh Home Credit</a></li>\r\n\n	<li>\r\n\n		<a href="http://tragopfpt.com/vi/html/id3/Dich-vu-tra-gop-cua-doi-tac-ACS/">C&ocirc;ng ty TNHH Thương mại ACS Việt Nam</a></li>\r\n\n</ul>\r\n\n<p>\r\n\n	Mọi thắc mắc cần được tư vấn qu&yacute; kh&aacute;ch c&oacute; thể li&ecirc;n hệ theo số Hotline: 098.928.1580</p>\r\n\n<p>\r\n\n	--------------------------------------------------------------------------------------------------------</p>\r\n\n<p>\r\n\n	Qu&yacute; Kh&aacute;ch Lưu &Yacute; : Hướng Dẫn Thủ Tục Trả G&oacute;p NHANH !</p>\r\n\n<p>\r\n\n	Để mua h&agrave;ng trả g&oacute;p với thủ tục nhanh gọn, Qu&yacute; kh&aacute;ch vui l&ograve;ng tải file : &nbsp;<a href="http://tragopfpt.com/userfiles/download/dangky.doc">Click</a></p>\r\n\n<p>\r\n\n	Điền th&ocirc;ng tin v&agrave; gửi file đến Email &nbsp;: info@tragopfpt.com</p>\r\n\n<p>\r\n\n	Sau khi Ch&uacute;ng t&ocirc;i nhận được mail của Qu&yacute; kh&aacute;ch,Ch&uacute;ng t&ocirc;i sẽ tiến h&agrave;ng ho&agrave;n tất thủ tục một c&aacute;ch nhanh gọn nhất.</p>\r\n\n<p>\r\n\n	Mọi thắc mắc cần được tư vấn qu&yacute; kh&aacute;ch c&oacute; thể li&ecirc;n hệ theo số Hotline:&nbsp;098.928.1580</p>\r\n\n<p>\r\n\n	Hồ Sơ Trả G&oacute;p Gồm :&nbsp;</p>\r\n\n<p>\r\n\n	- Chứng minh thư&nbsp;(&nbsp;Bản gốc v&agrave; photo&nbsp;)</p>\r\n\n<p>\r\n\n	- Hộ khẩu (&nbsp;Bản gốc v&agrave; photo&nbsp;)</p>\r\n\n<p>\r\n\n	-&nbsp;H&oacute;a đơn&nbsp;điện, nước, internet, điện thoại cố định... 1 trong 3 th&aacute;ng gần nhất</p>\r\n\n<p>\r\n\n	- Chứng minh thu nhập (&nbsp;nếu c&oacute;&nbsp;)</p>\r\n\n<p>\r\n\n	&nbsp;</p>\r\n\n<p>\r\n\n	Quy Định Về Thời Gian Trả G&oacute;p :&nbsp;</p>\r\n\n<p>\r\n\n	- Số tiền trả trước : Từ 30% - 70% gi&aacute; trị sản phẩm.</p>\r\n\n<p>\r\n\n	- Thời gian trả g&oacute;p : Từ 6 th&aacute;ng - 24 th&aacute;ng.</p>\r\n\n<p>\r\n\n	CH&Uacute;NG T&Ocirc;I CAM KẾT BẠN SẼ&nbsp;ĐƯỢC MUA TRẢ G&Oacute;P VỚI THỜI&nbsp; GIAN NHANH NHẤT&nbsp;TẠI FPT SHOP !&nbsp;</p>\r\n\n<p>\r\n\n	Xin ch&acirc;n trọng cảm ơn !</p>\r\n\n', 2, 1),
(3, 'GỬI TIẾT KIỆM TẠI TIÊNPHONGBANK, MUA HÀNG FPT SHOP ĐƯỢC GIẢM 50%', '<table cellpadding="0" cellspacing="0" class="main_content" width="100%">\r\n\n	<tbody>\r\n\n		<tr>\r\n\n			<td class="quote">\r\n\n				<p style="margin-top: 5px; margin-right: 0px; margin-bottom: 5px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; ">\r\n\n					Ng&acirc;n h&agrave;ng TMCP Ti&ecirc;n Phong (Ti&ecirc;nPhongBank) v&agrave; C&ocirc;ng ty TNHH B&aacute;n lẻ FPT (FPT Retail) phối hợp triển khai chương tr&igrave;nh chăm s&oacute;c kh&aacute;ch h&agrave;ng mang t&ecirc;n &ldquo;Gửi tiền - T&iacute;ch điểm&rdquo; từ ng&agrave;y 20/7 đến ng&agrave;y 20/10/2011. Chương tr&igrave;nh hứa hẹn sẽ mang lại nhiều lợi &iacute;ch cho kh&aacute;ch h&agrave;ng bằng h&igrave;nh thức cộng điểm để mua h&agrave;ng giảm gi&aacute;.</p>\r\n\n				<div style="display: block; text-align: center; ">\r\n\n					<img align="middle" border="0" height="249" hspace="0" src="http://tpb.com.vn/Resources/2011/07/19/1246982/img_5715.jpg" style="width: 363px; height: 249px; text-align: center; " width="363" /></div>\r\n\n			</td>\r\n\n		</tr>\r\n\n		<tr>\r\n\n			<td class="content">\r\n\n				<p style="margin-top: 5px; margin-right: 0px; margin-bottom: 5px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; ">\r\n\n					Theo đ&oacute;, kh&aacute;ch h&agrave;ng gửi tiết kiệm tại Ti&ecirc;nPhongBank ngo&agrave;i hưởng mức l&atilde;i suất tối ưu sẽ được FPT Retail tặng điểm thưởng để hưởng gi&aacute;m gi&aacute; tới 50% c&aacute;c mặt h&agrave;ng đang được ph&acirc;n phối tại hệ thống cửa h&agrave;ng FPT Shop ở TP. Hồ Ch&iacute; Minh, H&agrave; Nội, Đ&agrave; Nẵng, Hải Ph&ograve;ng như điện thoại, m&aacute;y ảnh, phụ kiện&hellip; Chỉ cần gửi tiết kiệm với số tiền v&agrave;i trăm triệu đồng trong kỳ hạn th&iacute;ch hợp, kh&aacute;ch h&agrave;ng sẽ t&iacute;ch đủ điểm để được giảm tới 50% gi&aacute; điện thoại iPhone, iPad c&ugrave;ng c&aacute;c mặt h&agrave;ng c&ocirc;ng nghệ hiện đại kh&aacute;c.</p>\r\n\n				<div style="display: block; text-align: center; ">\r\n\n					<img align="middle" border="0" height="386" hspace="0" src="http://tpb.com.vn/Resources/2011/07/19/6494216/anh_ct.jpg" style="width: 313px; height: 386px; " width="313" /></div>\r\n\n				<br />\r\n\n				<p style="margin-top: 5px; margin-right: 0px; margin-bottom: 5px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; ">\r\n\n					Trong khu&ocirc;n khổ chương tr&igrave;nh &ldquo;synergy&rdquo; nhằm kết hợp nội lực c&aacute;c đơn vị th&agrave;nh vi&ecirc;n tập đo&agrave;n FPT, Ti&ecirc;nPhongBank đang hợp t&aacute;c chặt chẽ với tất cả đơn vị trong tập đo&agrave;n nhằm mang đến cho kh&aacute;ch h&agrave;ng c&aacute;c dịch vụ tốt nhất, gi&aacute; cả hợp l&yacute; nhất. Theo những chương tr&igrave;nh dự định triển khai, kh&aacute;ch h&agrave;ng của Ti&ecirc;nPhongBank sẽ được hưởng ưu đ&atilde;i lớn khi sử dụng, mua b&aacute;n sản phẩm của c&aacute;c đơn vị th&agrave;nh vi&ecirc;n FPT v&agrave; ngược lại.</p>\r\n\n				<p style="margin-top: 5px; margin-right: 0px; margin-bottom: 5px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; ">\r\n\n					Th&ocirc;ng tin chi tiết, Qu&yacute; kh&aacute;ch vui l&ograve;ng li&ecirc;n hệ c&aacute;c điểm giao dịch của Ti&ecirc;nPhongBank hoặc gọi đến Hỗ trợ kh&aacute;ch h&agrave;ng: 1800.585885</p>\r\n\n			</td>\r\n\n		</tr>\r\n\n	</tbody>\r\n\n</table>\r\n\n<p>\r\n\n	&nbsp;</p>\r\n\n', 3, 1);

