CREATE TABLE IF NOT EXISTS `news` (
  `news_id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(225) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `short_info` text CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `info` text CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `cate_news_id` int(5) DEFAULT NULL,
  `image` text NOT NULL,
  `image_thumb` text NOT NULL,
  `active` tinyint(5) DEFAULT NULL,
  PRIMARY KEY (`news_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=8 ;

