INSERT INTO `news` (`news_id`, `name`, `short_info`, `info`, `cate_news_id`, `image`, `image_thumb`, `active`) VALUES
(3, 'Intel Pentium G620 + Gigabyte GA-H61M-S2V-B3: ', '<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">Theo như những tin tức gần đ&acirc;y cho thấy th&igrave; Intel đang c&oacute; kế hoạch kết th&uacute;c c&aacute;c bộ xử l&iacute; Intel Socket LGA775, đồng nghĩa với việc c&aacute;c nh&agrave; sản xuất bo mạch chủ tr&ecirc;n thế giới cũng sẽ chấm dứt v&ograve;ng đời của bo mạch chủ Intel c&ograve;n s&oacute;t lại cho đến thời điểm hiện tại l&agrave; G41.</span></p>\r\n\n', '<p>\r\n\n	<span style="font-family: arial,helvetica,sans-serif;">Chiếm tỉ lệ lớn, gần như to&agrave;n bộ thị trường phổ th&ocirc;ng, n&ecirc;n việc kết th&uacute;c d&ograve;ng đời socket LGA775 sẽ l&agrave;m ảnh hưởng kh&ocirc;ng nhỏ đến nhu cầu của người d&ugrave;ng trong thị trường n&agrave;y khi cần sắm bộ m&aacute;y t&iacute;nh c&oacute; mức gi&aacute; khoảng từ 6-8 triệu. Trong tầm gi&aacute; đ&oacute; th&igrave; d&ograve;ng LGA775 thực sự l&agrave; lựa chọn đ&uacute;ng đắn v&agrave; kh&ocirc;n ngoan, nếu lựa chọn LGA1156 th&igrave; sẽ kh&ocirc;ng hợp l&yacute; v&igrave; d&ograve;ng n&agrave;y cũng đ&atilde; ngừng sản xuất. Cuối c&ugrave;ng chỉ c&ograve;n lại mỗi series mới nhất l&agrave; Sandy Bridge Socket LGA1155 tuy nhi&ecirc;n cũng chưa thể l&agrave;m h&agrave;i l&ograve;ng kh&aacute;ch h&agrave;ng với c&aacute;i gi&aacute; c&ograve;n qu&aacute; cao.</span></p>\r\n\n<p>\r\n\n	<span style="font-family: arial,helvetica,sans-serif;">&nbsp;&nbsp;&nbsp; Vậy đ&acirc;u l&agrave; giải ph&aacute;p cho vấn đề n&agrave;y? Thực ra sau khi Intel tuy&ecirc;n bố sẽ kết th&uacute;c c&aacute;c CPU Socket LGA775, họ đ&atilde; c&oacute; phương &aacute;n sản xuất v&agrave; cho ra đời c&aacute;c d&ograve;ng CPU Pentium mới sử dụng Socket LGA1155 c&ugrave;ng kiến tr&uacute;c SandyBridge mạnh mẽ với c&aacute;c t&ecirc;n m&atilde; l&agrave; <strong>CPU Pentium G series (G620, G840, G850...)</strong> với mức gi&aacute; đề nghị từ nh&agrave; sản xuất Intel đưa ra l&agrave; dưới 100 USD. Mục đ&iacute;ch như đ&atilde; n&oacute;i ở tr&ecirc;n l&agrave; để trở th&agrave;nh người thay thế xứng đ&aacute;ng d&ograve;ng LGA775. B&ecirc;n cạnh d&ograve;ng CPU LGA1155 phổ th&ocirc;ng, Intel cũng tung ra d&ograve;ng chipset 6 series mới, cụ thể l&agrave; H61 để c&oacute; thể đảm bảo sự thay thế l&agrave; ho&agrave;n hảo nhất. Tất nhi&ecirc;n, được nhắm v&agrave;o thị trường phổ th&ocirc;ng n&ecirc;n th&ocirc;ng số kĩ thuật của H61 cũng được cắt giảm một số t&iacute;nh năng so với H67, P67 hay Z68 vốn nằm ở ph&acirc;n kh&uacute;c thị trường cao hơn.</span></p>\r\n\n<p>\r\n\n	<span style="font-family: arial,helvetica,sans-serif;">&nbsp;&nbsp;&nbsp; Ở thị trường Việt Nam n&oacute;i ri&ecirc;ng v&agrave; thế giới n&oacute;i chung, Gigabyte chiếm thị phần kh&aacute; lớn trong thị trường bo mạch chủ do đ&oacute; kh&ocirc;ng kh&oacute; hiểu khi h&atilde;ng đ&atilde; sớm cho ra đời c&aacute;c d&ograve;ng bo mạch chủ chipset H61. C&aacute;c sản phẩm H61 của Gigabyte c&oacute; kh&aacute; nhiều m&atilde;, tuy nhi&ecirc;n ở đ&acirc;y t&ocirc;i chọn ra sản phẩm thấp nhất l&agrave; H61M-S2V-B3 kết hợp c&ugrave;ng Intel Pentium G620 để xem liệu đ&acirc;y c&oacute; phải l&agrave; sự thay thế xứng đ&aacute;ng nền tảng LGA775 ở thị trường phổ th&ocirc;ng. Hệ thống LGA775 sử dụng c&aacute;c linh kiện tương tự, chỉ kh&aacute;c Mainboard v&agrave; CPU.</span></p>\r\n\n<p>\r\n\n	<span style="color: #ff0000; font-family: arial,helvetica,sans-serif;"><strong>Điểm qua H61M-S2V-B3</strong></span></p>\r\n\n<p>\r\n\n	<span style="font-family: arial,helvetica,sans-serif;">Mainboard H61M-S2V-B3 c&oacute; thiết kế nhỏ gọn k&iacute;ch thước microATX ph&ugrave; hợp với tất cả c&aacute;c loại vỏ m&aacute;y hiện đang c&oacute; tr&ecirc;n thị trường, đ&acirc;y cũng l&agrave; chuẩn k&iacute;ch thước phổ biến tr&ecirc;n c&aacute;c bo mạch chủ d&agrave;nh cho thị trường phổ th&ocirc;ng. C&aacute;c th&agrave;nh phần cũng như khe cắm mở rộng tr&ecirc;n H61M-S2V-B3 được trang bị vừa đủ với đa số nhu cầu sử dụng gồm 2 khe cắm Ram, 1 khe PCI-Express x16, 3 khe x1 v&agrave; 4 cổng SATA 2.</span></p>\r\n\n<div>\r\n\n	<span style="font-family: arial,helvetica,sans-serif;"><a class="highslide" href="http://www.gigabyte.com/fileupload/product/2/3875/4369_big.jpg"><img alt="" border="0" height="768" src="http://www.gigabyte.com/fileupload/product/2/3875/4369_big.jpg" style="max-width: 430px; width: 604px; height: 498px;" width="486" /></a></span></div>\r\n\n<div>\r\n\n	&nbsp;</div>\r\n\n<div>\r\n\n	<span style="font-family: arial,helvetica,sans-serif;">Kể từ năm 2011, Gigabyte đ&atilde; đặt ra ti&ecirc;u chuẩn sản xuất bo mạch chủ &aacute;p dụng với c&aacute;c d&ograve;ng sản phẩm bo mạch chủ của h&atilde;ng, v&agrave; ti&ecirc;u chuẩn n&agrave;y bao gồm 7 th&agrave;nh phần m&agrave; theo Gigabyte l&agrave; tất yếu phải c&oacute; tr&ecirc;n hầu hết c&aacute;c bo mạch chủ. Trong khu&ocirc;n khổ b&agrave;i viết t&ocirc;i sẽ kh&ocirc;ng nhắc lại c&aacute;c th&agrave;nh phần đ&oacute; v&igrave; đ&atilde; c&oacute; một b&agrave;i giới thiệu kh&aacute; chi tiết tại <strong><a href="http://thuylinh.com.vn/view_news.aspx?ncid=43&amp;nid=768"><span style="color: #0000ff;">đ&acirc;y</span></a></strong>, m&agrave; t&ocirc;i chỉ điểm qua một v&agrave;i trong số c&aacute;c th&agrave;nh phần ti&ecirc;u biểu tr&ecirc;n H61M-S2V-B3.</span></div>\r\n\n<div>\r\n\n	&nbsp;</div>\r\n\n<div>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;"><a class="highslide" href="http://diendanit.vn/imageupload/16644dfc51acf3669.jpg"><img alt="" src="http://diendanit.vn/imageupload/16644dfc51acf3669.jpg" /></a></span></p>\r\n\n	<p>\r\n\n		&nbsp;</p>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;">Trước hết về c&aacute;c phase điện cấp, theo ti&ecirc;u chuẩn của Gigabyte, tối thiểu tr&ecirc;n c&aacute;c mainboard của h&atilde;ng sẽ c&oacute; 4 phase điện, đối với H61M-S2V-B3 h&atilde;ng đ&atilde; trang bị 6 phase điện cấp, với mỗi phase điện l&agrave; sự kết hợp của bộ 3 linh kiện: tụ nh&ocirc;m Nhật, cuộn cảm l&otilde;i Ferrite v&agrave; MOSFET c&oacute; RDS(on) thấp. Về cơ bản, c&aacute;c phase điện l&agrave;m nhiệm vụ cung cấp đủ điện &aacute;p cho hoạt động của CPU, do đ&oacute;, lượng phase điện nhiều sẽ giảm tải tr&ecirc;n từng phase, giảm nhiệt độ trong qu&aacute; tr&igrave;nh sử dụng v&agrave; linh kiện sẽ c&oacute; độ bền cao hơn.</span></p>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;"><a class="highslide" href="http://diendanit.vn/imageupload/14234db297dfdb3e5.jpg"><img alt="" border="0" src="http://diendanit.vn/imageupload/14234db297dfdb3e5.jpg" style="max-width: 430px;" /></a></span></p>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;">Th&agrave;nh phần thứ 2 theo c&aacute; nh&acirc;n t&ocirc;i n&oacute; kh&aacute; hữu dụng trong thực tế qu&aacute; tr&igrave;nh sử dụng, đ&oacute; l&agrave; c&ocirc;ng nghệ On/Off Charge. C&ocirc;ng nghệ n&agrave;y gi&uacute;p c&aacute;c thiết bị giải tr&iacute; cầm tay sạc điện th&ocirc;ng qua cổng USB c&oacute; thể sạc được cả khi m&aacute;y t&iacute;nh đ&atilde; tắt, nguồn điện vẫn được duy tr&igrave; ở cổng USB trừ khi ngắt hẳn nguồn ch&iacute;nh.</span></p>\r\n\n	<p>\r\n\n		&nbsp;</p>\r\n\n	<div>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;"><a class="highslide" href="http://www.gigabyte.com/microsite/270/images/feature_28.jpg"><img alt="" border="0" src="http://www.gigabyte.com/microsite/270/images/feature_28.jpg" style="max-width: 430px;" /></a></span></div>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;">Trước đ&acirc;y c&ocirc;ng nghệ tương tự đ&atilde; từng xuất hiện nhưng chủ yếu l&agrave; c&oacute; tr&ecirc;n c&aacute;c m&aacute;y laptop. Đối với c&aacute;c hệ thống m&aacute;y b&agrave;n th&igrave; On/Off Charge đ&atilde; c&oacute; mặt tr&ecirc;n hầu hết c&aacute;c bo mạch chủ của Gigabyte. Đặc biệt, On/Off Charge c&ograve;n tối ưu hơn khi được kết hợp với c&ocirc;ng nghệ <strong><span style="color: blue;">USB Power 3X</span></strong> độc quyền của Gigabyte. Theo đ&oacute;, c&aacute;c cổng USB tr&ecirc;n H61M-S2V-B3 n&oacute;i ri&ecirc;ng v&agrave; c&aacute;c mainboard Gigabyte n&oacute;i chung sẽ c&oacute; d&ograve;ng điện cao hơn gấp 3 lần so với cổng USB chuẩn th&ocirc;ng thường. D&ograve;ng điện lớn hơn n&agrave;y sẽ r&uacute;t ngắn thời gian sạc pin cho c&aacute;c thiết bị kh&aacute; nhiều khi sạc pin th&ocirc;ng qua cổng USB.</span></p>\r\n\n	<div>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;"><a class="highslide" href="http://diendanit.vn/imageupload/16644dfc51ad5bf9b.jpg"><img alt="" border="0" height="470" src="http://diendanit.vn/imageupload/16644dfc51ad5bf9b.jpg" style="max-width: 430px;" width="602" /></a></span></div>\r\n\n	<p>\r\n\n		&nbsp;</p>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;"><strong>Dual BIOS</strong> &ndash; c&aacute;i t&ecirc;n kh&aacute; quen thuộc tr&ecirc;n c&aacute;c bo mạch chủ của Gigabyte. T&aacute;c dụng của Dual BIOS l&agrave; cho ph&eacute;p kh&ocirc;i phục nhanh v&agrave; an to&agrave;n khi BIOS gặp c&aacute;c trục trặc về dữ liệu lưu b&ecirc;n trong do Virus, tr&aacute;nh c&aacute;c rủi ro trong qu&aacute; tr&igrave;nh cập nhật BIOS.</span></p>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;"><a class="highslide" href="http://diendanit.vn/imageupload/14234d7881c758289.png"><img alt="" border="0" src="http://diendanit.vn/imageupload/14234d7881c758289.png" style="max-width: 430px;" /></a></span></p>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;">Tr&ecirc;n c&aacute;c mainboard Gigabyte kể từ năm 2011, c&ocirc;ng nghệ Dual BIOS được n&acirc;ng cấp l&ecirc;n để c&oacute; thể hỗ trợ nhận dạng v&agrave; sử dụng an to&agrave;n những ổ cứng c&oacute; dung lượng lớn từ 3TB trở l&ecirc;n.</span></p>\r\n\n	<div>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;"><a class="highslide" href="http://diendanit.vn/imageupload/14234d7881c750fa0.png"><img alt="" border="0" src="http://diendanit.vn/imageupload/14234d7881c750fa0.png" style="max-width: 430px;" /></a></span></div>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;">Về mặt &acirc;m thanh, H61M-S2V-B3 được trang bị chip sound Realtek ALC889 với th&ocirc;ng số SNR hay độ khuếch đại &acirc;m thanh đạt mức 108dB, nhỉnh hơn một ch&uacute;t so với c&aacute;c mainboard th&ocirc;ng thường với hệ số khuếch đại xấp xỉ khoảng 100dB. SNR l&agrave; tỉ số giữa t&iacute;n hiệu v&agrave; tạp &acirc;m trong qu&aacute; tr&igrave;nh khuếch đại &acirc;m thanh, con số n&agrave;y c&agrave;ng cao th&igrave; chất lượng &acirc;m thanh c&agrave;ng tốt do tỉ lệ nhiễu ảnh hưởng đến t&iacute;n hiệu thấp.</span></p>\r\n\n	<p>\r\n\n		&nbsp;</p>\r\n\n	<div>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;"><a class="highslide" href="http://www.gigabyte.com/fileupload/product/2/3875/4371.jpg"><img alt="" border="0" src="http://www.gigabyte.com/fileupload/product/2/3875/4371.jpg" style="max-width: 430px;" /></a></span></div>\r\n\n	<p>\r\n\n		&nbsp;</p>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;">Đối với c&aacute;c cổng giao tiếp ph&iacute;a sau, H61M-S2V-B3 được trang bị 2 cổng PS/2, 1 cổng D-sub v&agrave; 1 cổng DVI hỗ trợ xuất t&iacute;n hiệu ra song song 2 m&agrave;n h&igrave;nh, 6 cổng USB 2.0 với c&ocirc;ng nghệ USB Power 3x, 1 cổng LAN RJ45 v&agrave; 3 cổng sound analog.</span></p>\r\n\n	<p>\r\n\n		&nbsp;</p>\r\n\n	<div>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;"><a class="highslide" href="http://www.gigabyte.com/fileupload/product/2/3875/4370.jpg"><img alt="" border="0" src="http://www.gigabyte.com/fileupload/product/2/3875/4370.jpg" style="max-width: 430px;" /></a></span></div>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;">Nh&igrave;n chung thiết kế v&agrave; c&aacute;ch bố tr&iacute; c&aacute;c linh kiện, khe mở rộng tr&ecirc;n H61M-S2V-B3 vẫn đi theo qui chuẩn thiết kế mainboard th&ocirc;ng thường, c&aacute;c th&agrave;nh phần được bố tr&iacute; hợp l&iacute;, gọn g&agrave;ng tạo cảm gi&aacute;c chắc chắn khi cầm mainboard. Ngo&agrave;i ra c&oacute; thể dễ d&agrave;ng nhận thấy c&aacute;c chuẩn giao tiếp PATA hay FDD kh&ocirc;ng c&ograve;n được trang bị tr&ecirc;n H61M-S2V-B3, bởi thực tế sử dụng hiện nay c&aacute;c thiết bị sử dụng chuẩn giao tiếp n&agrave;y kh&ocirc;ng c&ograve;n nhiều, v&agrave; đ&atilde; được thay thế bởi c&aacute;c giao tiếp mới c&oacute; tốc độ cao hơn v&agrave; gọn g&agrave;ng hơn.</span></p>\r\n\n	<p>\r\n\n		<span style="color: #ff0000; font-family: arial,helvetica,sans-serif;"><strong>Hệ thống thử nghiệm</strong></span></p>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;">&nbsp;&nbsp;&nbsp; * <strong>CPU</strong>: <span style="color: #0000ff;">Intel&reg; Pentium&reg; Processor G620 (3M Cache, 2.60 GHz)</span> / Intel&reg; Core 2 Duo E7500 (3M Cache, 2.93Ghz)<br />\r\n\n		&nbsp;&nbsp;&nbsp; *<strong> Mainboard</strong>: <span style="color: #0000ff;">Gigabyte GA-H61M-S2V-B3</span>/ Gigabyte GA-G41MT-S2P<br />\r\n\n		&nbsp;&nbsp;&nbsp; * <strong>Ram</strong>: Kingmax 2GB 1333MHz<br />\r\n\n		&nbsp;&nbsp;&nbsp; *<strong> VGA</strong>: Onboard<br />\r\n\n		&nbsp;&nbsp;&nbsp; * <strong>HDD</strong>: SSD 80GB<br />\r\n\n		&nbsp;&nbsp;&nbsp; * <strong>PSU</strong>: CoolerMaster 600W</span></p>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;"><strong>C&aacute;c phần mềm v&agrave; Game test</strong>:</span></p>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;">3Dmark Vantage<br />\r\n\n		3Dmark 06</span></p>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;">Winrar<br />\r\n\n		Super PI 1M</span></p>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;">Devil May Cry 4<br />\r\n\n		Resident Evil 5</span></p>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;">Tất cả c&aacute;c thiết lập của phần mềm v&agrave; game đều để mặc định hoặc do hệ thống tự nhận, độ ph&acirc;n giải sử dụng l&agrave; 1280 x 1024</span></p>\r\n\n	<p>\r\n\n		<span style="color: #ff0000; font-family: arial,helvetica,sans-serif;"><strong>Kết Quả</strong></span></p>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;">Nhắm đến thị trường phổ th&ocirc;ng n&ecirc;n Intel Pentium G620 sẽ bị lược bỏ một v&agrave;i t&iacute;nh năng m&agrave; chỉ từ CPU i3-2100 trở l&ecirc;n mới c&oacute; như Video QuickSync, Clear Video HD,...Nhưng kh&ocirc;ng v&igrave; thế m&agrave; đồ họa t&iacute;ch hợp (IGP) điểm mạnh của d&ograve;ng SandyBridge ở CPU n&agrave;y bị l&agrave;m yếu đi, trong khu&ocirc;n khổ b&agrave;i viết ch&uacute;ng t&ocirc;i xin chỉ ra l&iacute; do v&igrave; sao bộ combo CPU Intel Sandy Bridge Pentium với main chipset H61 được coi l&agrave; sự thay thế xứng đ&aacute;ng cho d&ograve;ng Socket 775.</span></p>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;"><a class="highslide" href="http://i271.photobucket.com/albums/jj144/beubeu86/Intel%20SandyBridge%20Pentium%20G620%20vs%20Core%202%20Duo%20E7500/3Dmark.jpg"><img alt="" border="0" height="338" src="http://i271.photobucket.com/albums/jj144/beubeu86/Intel%20SandyBridge%20Pentium%20G620%20vs%20Core%202%20Duo%20E7500/3Dmark.jpg" style="max-width: 430px; width: 641px; height: 333px;" width="541" /></a></span></p>\r\n\n	<p>\r\n\n		<br />\r\n\n		<span style="font-family: arial,helvetica,sans-serif;">Hai chương tr&igrave;nh được c&aacute;c overclocker ưa chuộng để test hiệu năng của card đồ họa v&agrave; hệ thống m&aacute;y, tr&ecirc;n nền tảng LGA775 cũ c&oacute; vẻ <strong>hệ thống E7500 + G41MT-S2P kh&ocirc;ng theo kịp nh&acirc;n vật ch&iacute;nh trong b&agrave;i viết n&agrave;y l&agrave; combo Pentium G620 + H61M-S2V</strong> khi m&agrave; điểm số cả hai ch&ecirc;ch lệch nhau qu&aacute; xa.</span></p>\r\n\n	<div>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;"><a class="highslide" href="http://i271.photobucket.com/albums/jj144/beubeu86/Intel%20SandyBridge%20Pentium%20G620%20vs%20Core%202%20Duo%20E7500/GameBenchmark.jpg"><img alt="" border="0" height="380" src="http://i271.photobucket.com/albums/jj144/beubeu86/Intel%20SandyBridge%20Pentium%20G620%20vs%20Core%202%20Duo%20E7500/GameBenchmark.jpg" style="max-width: 430px; width: 635px; height: 393px;" width="567" /></a></span></div>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;">Chơi game người ta nghĩ ngay đến hiệu năng card đồ họa, sức mạnh cũng như gi&aacute; tiền của VGA c&agrave;ng lớn th&igrave; chạy game c&agrave;ng mịn v&agrave; mượt hơn (tất nhi&ecirc;n l&agrave; phải đi k&egrave;m với hệ thống tương đương), hai game đến từ nh&agrave; sản xuất nổi tiếng Nhật Bản Capcom được sử dụng trong b&agrave;i viết kh&ocirc;ng phải l&agrave; game c&oacute; cấu h&igrave;nh y&ecirc;u cầu cực cao nhưng cũng kh&ocirc;ng phải thuộc dạng tầm thường dễ xơi với những hệ thống đồ họa phổ th&ocirc;ng. <strong>Để chơi tốt th&igrave; frames (khung h&igrave;nh) của hai tr&ograve; bao giờ cũng phải từ 30 trở l&ecirc;n</strong>.<br />\r\n\n		<br />\r\n\n		Nh&igrave;n trong h&igrave;nh ta cũng c&oacute; thể thấy <strong>đồ họa onboard của main G41MT-S2P</strong> thật sự kh&ocirc;ng kham nổi sức mạnh đ&ograve;i hỏi của game Capcom, buộc phải chịu thua khi frames đứng chết ngất ở mức 6.5. <strong>Từ d&ograve;ng Sandy Bridge socket 1155 trở đi khi CPU v&agrave; GPU c&ugrave;ng sản xuất tr&ecirc;n c&ocirc;ng nghệ 32nm th&igrave; 2 th&agrave;nh phần n&agrave;y đ&atilde; được t&iacute;ch hợp lại với nhau th&agrave;nh một con chip duy nhất</strong> lại cho ch&uacute;ng ta c&aacute;i nh&igrave;n tương s&aacute;ng hơn về đồ họa t&iacute;ch hợp. Frames đạt được l&agrave; 28.22 hơn đồ họa t&iacute;ch hợp của chipset G41 kh&aacute; nhiều, mọi người c&oacute; thể sẽ băn khoăn kh&ocirc;ng hiểu với game online th&igrave; bộ combo mới n&agrave;y c&oacute; chơi tốt được kh&ocirc;ng ?<br />\r\n\n		<br />\r\n\n		Theo như những g&igrave; ch&uacute;ng t&ocirc;i biết c&oacute; một th&agrave;nh vi&ecirc;n tr&ecirc;n voz đ&atilde; thử chạy hệ thống <strong>CPU G620, main H61, ram DDR3 1333MHz cắm 6 accounts game Kiếm thế chơi ngon l&agrave;nh</strong>, xem ra việc đầu tư một hệ thống m&aacute;y như tr&ecirc;n cho một d&agrave;n net l&agrave; điều cực k&igrave; đ&aacute;ng xem x&eacute;t v&agrave;o thời điểm n&agrave;y</span></p>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;"><a class="highslide" href="http://i271.photobucket.com/albums/jj144/beubeu86/Intel%20SandyBridge%20Pentium%20G620%20vs%20Core%202%20Duo%20E7500/SuperPI.jpg"><img alt="" border="0" height="385" src="http://i271.photobucket.com/albums/jj144/beubeu86/Intel%20SandyBridge%20Pentium%20G620%20vs%20Core%202%20Duo%20E7500/SuperPI.jpg" style="max-width: 430px; width: 634px; height: 376px;" width="584" /></a></span></p>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;">L&agrave; chương tr&igrave;nh sử dụng c&aacute;c ph&eacute;p to&aacute;n t&iacute;nh số Pi ch&iacute;nh x&aacute;c tới 32 triệu chữ số sau dấu phẩy để đo khả năng t&iacute;nh to&aacute;n của CPU. Ph&eacute;p to&aacute;n thực hiện để t&iacute;nh ch&iacute;nh x&aacute;c số Pi đủ phức tạp để c&oacute; thể đ&aacute;nh gi&aacute; sức mạnh của CPU. <strong>SuperPi chủ yếu đ&aacute;nh gi&aacute; tốc độ xử l&yacute; của CPU v&agrave; Ram chứ kh&ocirc;ng tập trung v&agrave;o tốc độ của c&aacute;c th&agrave;nh phần kh&aacute;c</strong>, tr&ecirc;n đ&acirc;y l&agrave; những cơ sở cho việc ch&uacute;ng t&ocirc;i sử dụng phần mềm n&agrave;y để kiểm tra khả năng t&iacute;nh to&aacute;n của 2 CPU G620 v&agrave; E7500, cũng như khả năng tận dụng hiệu năng bộ nhớ Ram của 2 nền tảng n&agrave;y.<br />\r\n\n		<br />\r\n\n		<strong>Sử dụng mức t&iacute;nh 1 triệu chữ số sau dấu phẩy</strong>, thời gian c&agrave;ng ngắn tức l&agrave; khả năng t&iacute;nh to&aacute;n của CPU c&agrave;ng cao. Nh&igrave;n tr&ecirc;n biểu đồ c&oacute; thể thấy, <strong>Intel Pentium G620 socket LGA1155 ho&agrave;n th&agrave;nh ph&eacute;p t&iacute;nh trong thời gian 15.6s</strong>, c&ograve;n E7500 chậm hơn một ch&uacute;t khi mất tới gần 18s để ho&agrave;n th&agrave;nh ph&eacute;p t&iacute;nh.<br />\r\n\n		<br />\r\n\n		<br />\r\n\n		<strong>AIDA</strong> l&agrave; chương tr&igrave;nh muốn t&igrave;m hiểu về hệ thống của m&igrave;nh v&agrave; tạo ra điểm cho từng loại m&aacute;y t&iacute;nh. Đ&oacute; l&agrave; một cơ sở dẫn đến sự chiến thắng của c&ocirc;ng nghệ, <strong>tiền th&acirc;n được nhiều người biết đến với t&ecirc;n gọi l&agrave; phần mềm Everest</strong> nay đ&atilde; được đổi t&ecirc;n nhưng c&aacute;c t&iacute;nh năng ch&iacute;nh vẫn được giữ nguy&ecirc;n v&agrave; n&acirc;ng cấp dần đều qua c&aacute;c phi&ecirc;n bản<br />\r\n\n		<br />\r\n\n		Ở nền tảng LGA775 như mọi người đồ họa t&iacute;ch hợp (GPU) v&agrave; tr&igrave;nh điều khiển ram (Integrated Memory Controller) nằm trong Chipset Bắc (North Bridge), nhưng từ d&ograve;ng socket 1156 trở đi cả hai đ&atilde; được t&iacute;ch hợp trong CPU. Ph&eacute;p thử trong AIDA sẽ cho ch&uacute;ng ta thấy tốc độ xử l&iacute; cũng như độ trễ (Latency) của Ram sẽ nhanh hơn socket 775</span></p>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;"><strong>Pentium G620 + H61M-S2V-B3</strong>:</span></p>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;"><a class="highslide" href="http://diendanit.vn/imageupload/16644dfc5cc13199e.png"><img alt="" border="0" src="http://diendanit.vn/imageupload/16644dfc5cc13199e.png" style="max-width: 430px;" /></a></span></p>\r\n\n	<p>\r\n\n		&nbsp;</p>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;"><strong>Intel Core 2 Duo E7500 + G41MT-S2P</strong>:</span></p>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;"><a class="highslide" href="http://diendanit.vn/imageupload/16644dfc5cc17bdb6.png"><img alt="" border="0" src="http://diendanit.vn/imageupload/16644dfc5cc17bdb6.png" style="max-width: 430px;" /></a></span></p>\r\n\n	<p>\r\n\n		&nbsp;</p>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;"><strong>Cuối c&ugrave;ng l&agrave; Winzar</strong> l&agrave; phần mềm n&eacute;n/giải n&eacute;n phổ biến v&agrave; tốt nhất hiện nay. Tuy sinh sau người anh em l&agrave; WinZip nhưng hiện nay WinRAR đ&atilde; ho&agrave;n to&agrave;n qua mặt WinZip về chất lượng n&eacute;n file v&agrave; t&iacute;nh dễ sử dụng. Winzar cho ph&eacute;p thực hiện benchmark để kiểm tra hiệu năng hệ thống m&aacute;y t&iacute;nh dưới đ&acirc;y l&agrave; kết quả của:</span></p>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;"><strong>Intel Core 2 Duo E7500 + G41MT-S2P</strong>:</span></p>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;"><a class="highslide" href="http://i271.photobucket.com/albums/jj144/beubeu86/Intel%20SandyBridge%20Pentium%20G620%20vs%20Core%202%20Duo%20E7500/E7500_Winrar.png"><img alt="" border="0" src="http://i271.photobucket.com/albums/jj144/beubeu86/Intel%20SandyBridge%20Pentium%20G620%20vs%20Core%202%20Duo%20E7500/E7500_Winrar.png" style="max-width: 430px;" /></a></span></p>\r\n\n	<p>\r\n\n		&nbsp;</p>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;"><strong>Pentium G620 + H61M-S2V-B3</strong>:</span></p>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;"><a class="highslide" href="http://i271.photobucket.com/albums/jj144/beubeu86/Intel%20SandyBridge%20Pentium%20G620%20vs%20Core%202%20Duo%20E7500/G620_Winrar.png"><img alt="" border="0" src="http://i271.photobucket.com/albums/jj144/beubeu86/Intel%20SandyBridge%20Pentium%20G620%20vs%20Core%202%20Duo%20E7500/G620_Winrar.png" style="max-width: 430px;" /></a></span></p>\r\n\n	<p>\r\n\n		&nbsp;</p>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;">Kết quả cho thấy hệ thống hai hơn hệ thống một 18%<br />\r\n\n		<br />\r\n\n		Tổng kết lại ch&uacute;ng ta c&oacute;:</span></p>\r\n\n	<div>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;"><a class="highslide" href="http://i271.photobucket.com/albums/jj144/beubeu86/Intel%20SandyBridge%20Pentium%20G620%20vs%20Core%202%20Duo%20E7500/TongKet.jpg"><img alt="http://i271.photobucket.com/albums/jj144/beubeu86/Intel%20SandyBridge%20Pentium%20G620%20vs%20Core%202%20Duo%20E7500/TongKet-1.jpg" src="http://i271.photobucket.com/albums/jj144/beubeu86/Intel%20SandyBridge%20Pentium%20G620%20vs%20Core%202%20Duo%20E7500/TongKet-1.jpg" /></a></span></div>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;">Qua c&aacute;c ph&eacute;p thử benchmark c&oacute; thể đưa ra nhận x&eacute;t rằng việc <strong>Intel đưa ra những vi xử l&iacute; phổ th&ocirc;ng như Pentium G620 l&agrave; một bước đi đ&atilde; được t&iacute;nh to&aacute;n từ rất l&acirc;u</strong>, họ hiểu đ&atilde; đến l&uacute;c cần chấm dứt nền tảng LGA775 gi&agrave; cỗi m&agrave; thay v&agrave;o đ&oacute; một hệ thống mới như CPU G620 v&agrave; mainboard chipset H61, gi&aacute; cả hai sản phẩm đưa ra rất hợp l&iacute; v&agrave; hiệu năng như ch&uacute;ng ta đ&atilde; thấy xứng đ&aacute;ng thay thế cho LGA775.<strong> Đồng thời đ&acirc;y cũng l&agrave; combo kh&aacute; nặng k&iacute; của Intel để cạnh tranh với AMD trong ph&acirc;n kh&uacute;c thị trường phổ th&ocirc;ng</strong>.</span></p>\r\n\n	<p>\r\n\n		<span style="color: #ff0000; font-family: arial,helvetica,sans-serif;"><strong>Kết luận</strong></span></p>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;">Sẽ chẳng kh&oacute; khăn khi ch&uacute;ng ta nhận ra c&aacute;c qu&aacute;n net ở Việt Nam phần lớn sử dụng hệ thống m&aacute;y t&iacute;nh vi xử l&iacute; từ h&atilde;ng Intel, trong thời gian ngắn sắp tới khi CPU LGA775 kh&ocirc;ng c&ograve;n th&igrave; tất cả c&aacute;c hệ thống m&aacute;y phổ th&ocirc;ng sẽ l&agrave; sự kết hợp của CPU Pentium SandyBridge v&agrave; main H61 với c&aacute;c c&ocirc;ng nghệ mới, những t&iacute;nh năng mới v&agrave; đặc biệt l&agrave; đồ họa t&iacute;ch hợp sẽ vượt trội hơn rất nhiều.</span></p>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;"><strong>Số tiền đầu tư cho một hệ thống m&aacute;y đời mới</strong> t&iacute;ch hợp nhiều c&ocirc;ng nghệ t&acirc;n tiến, hiệu năng cao hơn kh&aacute; nhiều <strong>chỉ tương đương bộ m&aacute;y socket LGA775</strong> cho thấy những nh&agrave; sản xuất cỡ lớn Intel v&agrave; Gigabyte đang rất quan t&acirc;m tới thị trường kh&aacute;ch h&agrave;ng phổ th&ocirc;ng c&oacute; tiềm năng v&agrave; chiếm thị phần lớn ở nhiều nước như Việt Nam. Hi vọng trong tương lai gần sẽ c&oacute; nhiều nhiều đơn vị kinh doanh trong lĩnh vực thị trường m&aacute;y t&iacute;nh v&agrave; những kh&aacute;ch h&agrave;ng sẽ nhận biết được tiềm năng cũng như gi&aacute; trị của d&ograve;ng sản phẩm n&agrave;y.</span></p>\r\n\n	<p>\r\n\n		<span style="font-family: arial,helvetica,sans-serif;">(Theo Thủy Linh)</span></p>\r\n\n</div>\r\n\n<p>\r\n\n	&nbsp;</p>\r\n\n', 2, 'upload/news/resize_216_toan-the.jpg', 'upload/news/resize_216_toan-the_thumb.jpg', 1),
(2, 'Hanoicomputer – 10 năm một chặng đường', '<div id="news_detail_summary">\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">C&ocirc;ng ty M&aacute;y T&iacute;nh H&agrave; Nội (Hanoicomputer) l&agrave; một trong những doanh nghiệp h&agrave;ng đầu hoạt động trong lĩnh vực kinh doanh c&aacute;c sản phẩm CNTT. Th&agrave;nh lập từ năm 2001 đến nay, c&ocirc;ng ty đ&atilde; tạo được chỗ đứng vững chắc tr&ecirc;n thị trường b&aacute;n bu&ocirc;n, b&aacute;n lẻ, trở th&agrave;nh thương hiệu quen thuộc v&agrave; l&agrave; đối t&aacute;c tin cậy của nhiều bạn h&agrave;ng trong nước v&agrave; Quốc tế</span></div>\r\n\n<div style="text-align: left;">\r\n\n	&nbsp;</div>\r\n\n', '<p>\r\n\n	Với 10 năm hoạt động, 10 năm khẳng định thương hiệu tr&ecirc;n thị trường c&ocirc;ng nghệ th&ocirc;ng tin với bao khó khăn v&agrave; thử th&aacute;ch, 10 năm nỗ lực v&agrave; phấn đấu kh&ocirc;ng ngừng, Hanoicomputer mong muốn trở th&agrave;nh người bạn đồng h&agrave;nh kh&ocirc;ng thể thiếu đối với Qu&yacute; kh&aacute;ch h&agrave;ng, đồng thời đ&aacute;p ứng nhu cầu ng&agrave;y c&agrave;ng cao của thị trường ti&ecirc;u thụ sản phẩm c&ocirc;ng nghệ th&ocirc;ng tin tr&ecirc;n địa b&agrave;n H&agrave; Nội v&agrave; c&aacute;c tỉnh th&agrave;nh l&acirc;n cận. Có thể nói, th&agrave;nh c&ocirc;ng lớn nhất m&agrave; Hanoicomputer nhận được ch&iacute;nh l&agrave; sự tin tưởng, y&ecirc;u mến v&agrave; ủng hộ của to&agrave;n thể Qu&yacute; kh&aacute;ch h&agrave;ng! Hiện nay, c&ocirc;ng ty đ&atilde; c&oacute; một hệ thống kh&aacute;ch h&agrave;ng ổn định trải d&agrave;i từ Bắc đến Nam. Lu&ocirc;n lấy yếu tố h&agrave;i h&ograve;a về lợi &iacute;ch l&agrave;m nền tảng, l&atilde;nh đạo C&ocirc;ng ty hiểu rằng, niềm tin của kh&aacute;ch h&agrave;ng về gi&aacute; th&agrave;nh, chất lượng v&agrave; dịch vụ l&agrave; sự sống c&ograve;n của C&ocirc;ng ty. Do vậy, mọi hoạt động kinh doanh của C&ocirc;ng ty lu&ocirc;n hướng tới mục ti&ecirc;u t&ocirc;n trọng v&agrave; bảo đảm quyền lợi cho kh&aacute;ch h&agrave;ng, chinh phục kh&aacute;ch h&agrave;ng bằng chất lượng sản phẩm v&agrave; dịch vụ tối ưu</p>\r\n\n<div>\r\n\n	&nbsp;</div>\r\n\n<div>\r\n\n	<img alt="" border="0" src="http://www.hanoicomputer.vn/uploads/showroom.jpg" width="700" /></div>\r\n\n<div>\r\n\n	Với lịch sử th&agrave;nh lập vừa tr&ograve;n 1 thập kỷ, C&ocirc;ng ty M&aacute;y T&iacute;nh H&agrave; Nội tự h&agrave;o l&agrave; đơn vị ti&ecirc;n phong trong lĩnh vực cung cấp c&aacute;c sản phẩm CNTT tới h&agrave;ng triệu người tr&ecirc;n cả nước.&nbsp;Mười năm- một chặng đường x&acirc;y dựng, trưởng th&agrave;nh v&agrave; ph&aacute;t triển với bao thăng trầm của thời đại v&agrave; h&ocirc;m nay đ&acirc;y, C&ocirc;ng ty M&aacute;y T&iacute;nh H&agrave; Nội c&oacute; thể ho&agrave;n to&agrave;n tự h&agrave;o với những g&igrave; đ&atilde; đạt được, đặc biệt l&agrave; th&agrave;nh quả của những năm đầu hội nhập đầy kh&oacute; khăn cũng như phải đương đầu với t&igrave;nh h&igrave;nh biến động kinh tế trong nước&nbsp; v&agrave; thế giới theo chiều hướng phức tạp, kh&oacute; lường.</div>\r\n\n<div>\r\n\n	&nbsp;</div>\r\n\n<div>\r\n\n	Khởi đầu với những th&aacute;ch thức v&agrave; kh&oacute; khăn sau 10 năm ho&agrave;n th&agrave;nh sứ mệnh đưa c&aacute;c sản phẩm c&ocirc;ng nghệ tới người ti&ecirc;u d&ugrave;ng, C&ocirc;ng ty M&aacute;y T&iacute;nh H&agrave; Nội đ&atilde; v&agrave; đang ho&agrave;n thiện m&igrave;nh để n&acirc;ng tầm gi&aacute; trị mới&nbsp;(Gi&aacute; trị của niềm tin Việt).&nbsp;nhằm thỏa m&atilde;n nhu cầu bất tận của những con người Việt kh&aacute;t khao cống hiến, l&agrave;m việc tr&igrave;nh độ cao.&nbsp;</div>\r\n\n<div>\r\n\n	&nbsp;</div>\r\n\n<div>\r\n\n	Để đạt được những th&agrave;nh quả như ng&agrave;y h&ocirc;m nay cũng ch&iacute;nh l&agrave; nhờ tinh thần đo&agrave;n kết, gắn b&oacute;, tận tuỵ, hết l&ograve;ng, hết sức của tập thể Ban Gi&aacute;m Đốc, nh&acirc;n vi&ecirc;n trong to&agrave;n c&ocirc;ng ty. Sự tin tưởng của nh&agrave; đầu tư, sự hợp t&aacute;c nhiệt t&igrave;nh của qu&yacute; đối t&aacute;c v&agrave; kh&aacute;ch h&agrave;ng gần xa trong suốt thời gian qua. Tất cả v&igrave; sự trường tồn v&agrave; ph&aacute;t triển vững chắc của C&ocirc;ng ty M&aacute;y T&iacute;nh H&agrave; Nội.</div>\r\n\n<div>\r\n\n	<p>\r\n\n		&nbsp;</p>\r\n\n</div>\r\n\n<div>\r\n\n	&nbsp;</div>\r\n\n<div>\r\n\n	Trong thời gian h&igrave;nh th&agrave;nh v&agrave; ph&aacute;t triển c&ocirc;ng ty kh&ocirc;ng ngừng cải thiện chất lượng dịch vụ để&nbsp; lu&ocirc;n lu&ocirc;n v&agrave; sẵn s&agrave;ng phục vụ qu&yacute; kh&aacute;ch h&agrave;ng 1 c&aacute;ch tốt nhất. Con người, chất lượng, uy t&iacute;n, gi&aacute; cả&nbsp; l&agrave; những ti&ecirc;u ch&iacute; m&agrave; c&ocirc;ng ty lu&ocirc;n t&igrave;m kiếm cho sự phục vụ l&acirc;u d&agrave;i v&agrave; vững mạnh của m&igrave;nh.</div>\r\n\n<div>\r\n\n	&nbsp;</div>\r\n\n<div>\r\n\n	&nbsp;</div>\r\n\n<div>\r\n\n	&nbsp;</div>\r\n\n<div>\r\n\n	<em><em><img alt="" border="0" src="http://www.hanoicomputer.vn/uploads/IMG_8981_SN.jpg" width="700" /></em></em></div>\r\n\n<p>\r\n\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p>\r\n\n<div>\r\n\n	&nbsp;</div>\r\n\n<div>\r\n\n	Với đội ngũ nh&acirc;n sự được đ&agrave;o tạo chuy&ecirc;n s&acirc;u, c&oacute; tay nghề cao, một bộ m&aacute;y l&agrave;m việc hiệu quả trong v&ograve;ng 10 năm với những đột ph&aacute; về dịch vụ, sự lớn mạnh của C&ocirc;ng ty đ&atilde; minh chứng cho điều đ&oacute;. Sự l&atilde;nh đạo v&agrave; tầm nh&igrave;n tương lai s&acirc;u rộng của Ban Gi&aacute;m Đốc hứa hẹn mang tới nhiều th&agrave;nh c&ocirc;ng &nbsp;hơn nữa những chuẩn mực trong ng&agrave;nh dịch vụ c&ocirc;ng nghệ.</div>\r\n\n<div>\r\n\n	&nbsp;</div>\r\n\n<div>\r\n\n	Trong lần kỷ niệm sinh nhật 10 năm của C&ocirc;ng ty M&aacute;y T&iacute;nh H&agrave; Nội HNC,&nbsp; ch&uacute;ng t&ocirc;i xin gửi lời tri &acirc;n v&agrave; cảm ơn ch&acirc;n th&agrave;nh s&acirc;u sắc tới mọi qu&yacute; kh&aacute;ch h&agrave;ng đ&atilde; gắn b&oacute; với ch&uacute;ng t&ocirc;i trong thời gian qua. Với sứ mệnh đưa c&ocirc;ng nghệ tới người ti&ecirc;u d&ugrave;ng, c&ocirc;ng ty M&aacute;y T&iacute;nh H&agrave; Nội sẽ cố gắng phấn đấu nhiều hơn nữa cho sự ph&aacute;t triển của một tương lai vững bền.</div>\r\n\n<div>\r\n\n	&nbsp;</div>\r\n\n<div>\r\n\n	<em><strong>Nh&acirc;n vi&ecirc;n M&aacute;y T&iacute;nh H&agrave; Nội lu&ocirc;n nh&atilde; nhặn, niềm nở, lịch sự khi giao tiếp với kh&aacute;ch h&agrave;ng, tối đa lợi &iacute;ch kh&aacute;ch h&agrave;ng!</strong></em></div>\r\n\n<div>\r\n\n	<p>\r\n\n		&nbsp;</p>\r\n\n	<p>\r\n\n		<strong>HANOICOMPUTER </strong>x&acirc;y dựng một đội ngũ nh&acirc;n vi&ecirc;n với phong c&aacute;ch l&agrave;m việc chuy&ecirc;n nghiệp, lu&ocirc;n đ&uacute;ng giờ, đ&uacute;ng hẹn với kh&aacute;ch h&agrave;ng v&agrave; đối t&aacute;c. Lu&ocirc;n học hỏi v&agrave; s&aacute;ng tạo kh&ocirc;ng ngừng, sẵn s&agrave;ng lắng nghe mọi phản hồi của kh&aacute;ch h&agrave;ng, đối t&aacute;c để kịp thời điều chỉnh hợp l&yacute; những th&ocirc;ng tin đ&oacute;<strong>.</strong></p>\r\n\n	<p>\r\n\n		&nbsp;</p>\r\n\n	<p>\r\n\n		<em>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt="" border="0" src="http://www.hanoicomputer.vn/uploads/ML3.jpg" width="700" /></em></p>\r\n\n	<p>\r\n\n		&nbsp;</p>\r\n\n	<p>\r\n\n		&nbsp;</p>\r\n\n	<p>\r\n\n		&nbsp;</p>\r\n\n	<p>\r\n\n		<strong><em><strong>M&aacute;y T&iacute;nh H&agrave; Nội x&acirc;y dựng, duy tr&igrave; v&agrave; ph&aacute;t triển hệ thống ph&acirc;n phối mạnh v&agrave; &quot;phủ s&oacute;ng&quot; rộng khắp!</strong></em>&nbsp; </strong></p>\r\n\n	<p>\r\n\n		&nbsp;</p>\r\n\n</div>\r\n\n<div>\r\n\n	<strong>HANOICOMPUTER </strong>lu&ocirc;n coi kh&aacute;ch h&agrave;ng l&agrave; những đối t&aacute;c quan trọng trong kinh doanh. C&ocirc;ng ty lu&ocirc;n đặt quyền lợi của kh&aacute;ch h&agrave;ng l&ecirc;n h&agrave;ng đầu, coi chất lượng sản phẩm v&agrave; dịch vụ l&agrave; chiến lược sống c&ograve;n của c&ocirc;ng ty</div>\r\n\n<div>\r\n\n	<p>\r\n\n		&nbsp;</p>\r\n\n	<p>\r\n\n		&nbsp;</p>\r\n\n	<p>\r\n\n		<em><strong>M&aacute;y T&iacute;nh H&agrave; Nội trở th&agrave;nh nh&agrave; ph&acirc;n phối c&oacute; t&ecirc;n tuổi v&agrave; uy t&iacute;n tr&ecirc;n thị trường</strong></em><strong><em>!</em></strong></p>\r\n\n	<p>\r\n\n		&nbsp;</p>\r\n\n	<p>\r\n\n		&nbsp;</p>\r\n\n	<p>\r\n\n		Kh&aacute;t vọng mở rộng, vươn xa, chinh phục kh&aacute;ch h&agrave;ng, mong muốn tối đa lợi &iacute;ch kh&aacute;ch h&agrave;ng đ&atilde; th&uacute;c đẩy C&ocirc;ng ty M&aacute;y t&iacute;nh H&agrave; Nội phải nỗ lực hết m&igrave;nh để tạo tầm ảnh hưởng tr&ecirc;n địa b&agrave;n H&agrave; Nội cũng như c&aacute;c tỉnh th&agrave;nh trong cả nước v&agrave; mở rộng c&aacute;c cơ hội giao thương trong nước v&agrave; quốc tế cho ch&iacute;nh m&igrave;nh.</p>\r\n\n	<div>\r\n\n		Với mong muốn vươn l&ecirc;n trở th&agrave;nh một trong những doanh nghiệp h&agrave;ng đầu Việt Nam trong việc ph&acirc;n phối v&agrave; lắp r&aacute;p c&aacute;c sản phẩm, vật tư, linh kiện v&agrave; cung cấp c&aacute;c dịch vụ về Điện tử - Tin học - Viễn th&ocirc;ng, c&ocirc;ng ty M&aacute;y t&iacute;nh H&agrave; Nội cam kết:</div>\r\n\n	<p>\r\n\n		&nbsp;</p>\r\n\n	<p>\r\n\n		&nbsp;</p>\r\n\n	<p>\r\n\n		<strong>Với phương ch&acirc;m</strong>:</p>\r\n\n</div>\r\n\n<div>\r\n\n	<strong>-&nbsp;M</strong>ang lại hiệu quả cao nhất cho kh&aacute;ch h&agrave;ng, cho đối t&aacute;c, cho x&atilde; hội gắn liền với lợi &iacute;ch của c&ocirc;ng ty</div>\r\n\n<div>\r\n\n	<strong>-&nbsp;T</strong>ạo ưu thế cạnh tranh bằng c&aacute;c giải ph&aacute;p tổng thể, cung cấp đa dạng c&aacute;c thiết bị tin học v&agrave; phần mềm ứng dụng c&oacute; chất &nbsp; lượng v&agrave; độ tin cậy cao</div>\r\n\n<div>\r\n\n	<strong>-&nbsp;H</strong>uấn luyện, đ&agrave;o tạo li&ecirc;n tục về chuy&ecirc;n m&ocirc;n, nghiệp vụ cho tất cả nh&acirc;n vi&ecirc;n để mang lại cho kh&aacute;ch h&agrave;ng niềm tin tưởng ở &nbsp;&nbsp;khả năng chuy&ecirc;n m&ocirc;n, t&iacute;nh chu đ&aacute;o với c&aacute;c dịch vụ bảo h&agrave;nh, bảo tr&igrave; sau b&aacute;n h&agrave;ng tốt nhất.</div>\r\n\n<div>\r\n\n	<strong>-&nbsp;N</strong>&acirc;ng cao chất lượng v&agrave; gi&aacute; trị của sản phẩm, dịch vụ th&ocirc;ng qua hoạt động tư vấn, đ&agrave;o tạo cho kh&aacute;ch h&agrave;ng để lựa chọn v&agrave; &nbsp;&nbsp;ứng dụng tốt nhất</div>\r\n\n<div>\r\n\n	&nbsp;</div>\r\n\n<div>\r\n\n	Đến với M&aacute;y T&iacute;nh H&agrave; Nội, qu&yacute; kh&aacute;ch h&agrave;ng sẽ c&oacute; cơ hội sở hữu những sản phẩm c&ocirc;ng nghệ cao cấp với gi&aacute; cả rất hợp l&yacute; k&egrave;m theo những phần qu&agrave; hấp dẫn nhất trong c&aacute;c chương tr&igrave;nh khuyến mại.</div>\r\n\n<div>\r\n\n	&nbsp;</div>\r\n\n<div>\r\n\n	Lu&ocirc;n nỗ lực vươn l&ecirc;n v&agrave; n&acirc;ng cao chất lượng dịch vụ, Hanoicomputer lu&ocirc;n coi &ldquo;kh&aacute;ch h&agrave;ng l&agrave; &acirc;n nh&acirc;n&rdquo; của m&igrave;nh. Hanoicomputer cam kết đem lại cho qu&yacute; kh&aacute;ch h&agrave;ng ưu đ&atilde;i lớn nhất, sự phục vụ tận t&igrave;nh, chu đ&aacute;o nhất để đ&aacute;p lại sự tin y&ecirc;u m&agrave; qu&yacute; kh&aacute;ch d&agrave;nh cho M&aacute;y T&iacute;nh H&agrave; Nội trong suốt 10 năm qua</div>\r\n\n<div>\r\n\n	<p>\r\n\n		<strong>Hanoicomputer &ldquo;Uy t&iacute;n tạo dựng niềm tin&rdquo;!</strong></p>\r\n\n	<div>\r\n\n		&nbsp;</div>\r\n\n</div>\r\n\n<div>\r\n\n	&nbsp;</div>\r\n\n<div>\r\n\n	<p>\r\n\n		&nbsp;Một số h&igrave;nh ảnh về Hanoicomputer</p>\r\n\n</div>\r\n\n<div>\r\n\n	<p>\r\n\n		<img alt="" border="0" src="http://www.hanoicomputer.vn/uploads/DSC08778.jpg" width="700" /></p>\r\n\n</div>\r\n\n<div>\r\n\n	<p>\r\n\n		&nbsp;</p>\r\n\n</div>\r\n\n<p>\r\n\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p>\r\n\n<p>\r\n\n	<img alt="" border="0" src="http://www.hanoicomputer.vn/uploads/IMG_0789.jpg" width="700" /></p>\r\n\n<p>\r\n\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p>\r\n\n<p>\r\n\n	<img alt="" border="0" src="http://www.hanoicomputer.vn/uploads/IMG_8989_SN.jpg" width="700" /></p>\r\n\n', 2, 'upload/news/resize_282_logo.jpg', 'upload/news/resize_282_logo_thumb.jpg', 1),
(5, 'Thông báo nghỉ tết âm lịch 2011', '<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">C&ocirc;ng ty TNHH M&aacute;y t&iacute;nh H&agrave; Nội xin tr&acirc;n trọng th&ocirc;ng b&aacute;o tới to&agrave;n thể Qu&yacute; kh&aacute;ch h&agrave;ng v&agrave; đối t&aacute;c lịch nghỉ tết &acirc;m lịch 2011.</span></p>\r\n\n', '<p>\r\n\n	<span>Năm 2011 đang đến gần, HanoiComputer xin gửi lời ch&uacute;c mừng năm mới cũng như lời cảm ơn tr&acirc;n trọng nhất tới to&agrave;n thể Qu&yacute; kh&aacute;ch h&agrave;ng v&agrave; đối t&aacute;c đ&atilde; lu&ocirc;n ủng hộ, gi&uacute;p đỡ HanoiComputer&nbsp;trong thời gian qua. K&iacute;nh ch&uacute;c Qu&yacute; kh&aacute;ch h&agrave;ng v&agrave; đối t&aacute;c một năm mới&nbsp;<strong>&ldquo;An Khang &nbsp;&ndash;&nbsp; Thịnh Vượng&rdquo;</strong></span></p>\r\n\n<p>\r\n\n	<span>Một năm mới&nbsp; hứa hẹn nhiều th&agrave;nh c&ocirc;ng v&agrave; th&aacute;ch thức đang đến gần, HanoiComputer&nbsp;cam kết sẽ lu&ocirc;n lu&ocirc;n phấn đấu để l&agrave;m vừa l&ograve;ng nhất c&aacute;c Qu&yacute; kh&aacute;ch h&agrave;ng bằng những sản phẩm chất lượng nhất, dịch vụ tốt nhất v&agrave; gi&aacute; cả ưu đ&atilde;i nhất.</span><br />\r\n\n	<br />\r\n\n	<span>Để Qu&yacute; kh&aacute;ch h&agrave;ng v&agrave; đối t&aacute;c thuận tiện v&agrave; chủ động trong việc sắp xếp thời gian giao dịch của m&igrave;nh với HanoiComputer, ch&uacute;ng t&ocirc;i xin tr&acirc;n trọng th&ocirc;ng b&aacute;o lịch nghỉ tết &acirc;m lịch như sau:</span></p>\r\n\n<p>\r\n\n	<br />\r\n\n	<strong><span>HanoiComputer&nbsp;sẽ tạm dừng giao dịch ở tất cả c&aacute;c showroom từ 17h30 ng&agrave;y 01 th&aacute;ng 02 năm 2011 đến hết ng&agrave;y 07 th&aacute;ng 02 năm 2011.&nbsp;</span></strong><strong><span>Ng&agrave;y 08 th&aacute;ng 02 năm 2011 (tức m&ugrave;ng 6 Tết &acirc;m lịch), ch&uacute;ng t&ocirc;i sẽ trở lại l&agrave;m việc b&igrave;nh thường.</span></strong></p>\r\n\n<p style="text-align: center;">\r\n\n	<strong>&nbsp;</strong><br />\r\n\n	<span>Một lần nữa, xin ch&uacute;c tất cả Qu&yacute; kh&aacute;ch h&agrave;ng v&agrave; đối t&aacute;c một năm mới</span></p>\r\n\n<p style="text-align: center;">\r\n\n	<br />\r\n\n	<span style="font-size: large; color: #ff0000;"><strong>&ldquo;AN KHANG &nbsp;&ndash; THỊNH VƯỢNG&rdquo;</strong></span></p>\r\n\n<p style="text-align: center;">\r\n\n	&nbsp;</p>\r\n\n<p style="text-align: center;">\r\n\n	<span style="font-size: large; color: #ff0000;"><strong><img alt="" height="480" src="http://www.hanoicomputer.vn/Images/News/73_new_year_2011_888853_1292035595.jpg" width="640" /></strong></span></p>\r\n\n', 2, 'upload/news/resize_73_new_year_2011_888853_1292035595.jpg', 'upload/news/resize_73_new_year_2011_888853_1292035595_thumb.jpg', 1);
INSERT INTO `news` (`news_id`, `name`, `short_info`, `info`, `cate_news_id`, `image`, `image_thumb`, `active`) VALUES
(4, 'Mua hàng trả góp tại HanoiComputer', '<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">Kh&ocirc;ng cần phải c&oacute; đủ tiền để sở hữu sản phẩm m&igrave;nh y&ecirc;u th&iacute;ch, chỉ cần 1 khoản tiền nhỏ ban đầu l&agrave; bạn đ&atilde; c&oacute; thể sở hữu được sản phẩm m&igrave;nh mong muốn!</span></p>\r\n\n', '<div>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">Kh&aacute;ch h&agrave;ng l&agrave; c&ocirc;ng d&acirc;n Việt Nam tuổi từ&nbsp;21 đến 60 tuổi (ri&ecirc;ng đối tượng Sinh Vi&ecirc;n từ 18 trở l&ecirc;n) đều c&oacute; thể tham gia mua h&agrave;ng trả g&oacute;p tại HanoiComputer. C&aacute;c ưu điểm khi mua h&agrave;ng trả g&oacute;p tại HanoiComputer:</span></div>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- L&atilde;i suất 2,2% (t&iacute;nh theo dư nợ ban đầu).</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Kỳ hạn vay rộng (06, 09, 12, 18 ,24 th&aacute;ng).</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Hỗ trợ đối tượng kh&aacute;ch h&agrave;ng c&oacute; hộ khẩu ngoại tỉnh, H&agrave; Nội đang sinh sống, học tập v&agrave; l&agrave;m việc tại H&agrave; Nội.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Đặc biệt hỗ trợ đối tượng đ&atilde; nghỉ hưu, qu&acirc;n nh&acirc;n v&agrave; c&ocirc;ng an.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel"><strong>ĐỐI VỚI KH&Aacute;CH H&Agrave;NG L&Agrave; SINH VI&Ecirc;N:</strong></span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- L&agrave; c&ocirc;ng d&acirc;n Việt Nam đang học tập tại H&agrave; Nội.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Từ 18 tuổi trở l&ecirc;n.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- C&oacute; người đứng ra bảo l&atilde;nh (người bảo l&atilde;nh l&agrave; bố, mẹ, anh, chị, em ruột).</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Mức thu nhập của người đứng ra bảo l&atilde;nh từ 1,500,000 vnd trở l&ecirc;n.</span></p>\r\n\n<ul>\r\n\n	<li>\r\n\n		<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">Nếu l&agrave; bố, mẹ bảo l&atilde;nh: hộ khẩu của bố mẹ ở c&aacute;c tỉnh hoặc ở H&agrave; Nội đều được.</span></li>\r\n\n	<li>\r\n\n		<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">Nếu l&agrave; anh, chị, em ruột: phải đang sinh sống, l&agrave;m việc tại H&agrave; Nội.</span></li>\r\n\n</ul>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel"><strong>Những giấy tờ cần chuẩn bị:</strong></span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- CMND photo.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Sổ hộ khẩu nguy&ecirc;n cuấn photo.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- H&oacute;a đơn x&aacute;c nhận địa chỉ nơi ở của người mua h&agrave;ng.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Thẻ sinh vi&ecirc;n photo.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- CMND của người đứng ra bảo l&atilde;nh.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel"><strong><span style="text-decoration: underline;">Ch&uacute; &yacute;:</span></strong></span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Sinh vi&ecirc;n ngoại tỉnh đang học tập tại H&agrave; Nội được ưu ti&ecirc;n hỗ trợ mua trả g&oacute;p.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Người bảo l&atilde;nh kh&ocirc;ng cần đến k&yacute; nhận giấy tờ (thuận tiện cho đối tượng c&oacute; người bảo l&atilde;nh đang cư tr&uacute; ở c&aacute;c tỉnh xa H&agrave; Nội).</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Nếu vay tr&ecirc;n 10 triệu,&nbsp; người đứng ra bảo l&atilde;nh phải cung cấp giấy tờ chứng minh thu nhập (giấy tờ rất đơn giản).</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Tất cả giấy tờ l&agrave; bản photo kh&ocirc;ng cần c&ocirc;ng chứng.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- H&oacute;a đơn x&aacute;c nhận địa chỉ nơi ở của người mua h&agrave;ng: h&oacute;a đơn tiền điện, h&oacute;a đơn tiền nước, h&oacute;a đơn tiền internet, h&oacute;a đơn truyền h&igrave;nh c&aacute;p, h&oacute;a đơn điện thoại di động trả sau, giấy tạm tr&uacute; &hellip;</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel"><strong>ĐỐI VỚI KH&Aacute;CH H&Agrave;NG L&Agrave; NGƯỜI Đ&Atilde; ĐI L&Agrave;M:</strong></span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- L&agrave; c&ocirc;ng d&acirc;n Việt Nam đang l&agrave;m việc tại H&agrave; Nội.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Từ 21 &ndash; 60 tuổi.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Thu nhập từ 1,500,000 vnd trở l&ecirc;n.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel"><strong>Những giấy tờ cần chuẩn bị:</strong></span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- CMND photo.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Sổ hộ khẩu nguy&ecirc;n cuấn photo.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- H&oacute;a đơn x&aacute;c nhận địa chỉ nơi ở của người mua h&agrave;ng.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel"><strong><span style="text-decoration: underline;">Ch&uacute; &yacute;:</span></strong></span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Người đi l&agrave;m c&oacute; hộ khẩu ở H&agrave; Nội hoặc ngoại tỉnh v&agrave; đang l&agrave;m việc tại H&agrave; Nội cũng được hỗ trợ. Đặc biệt hỗ trợ đối tượng C&ocirc;ng An, Bộ Đội, Gi&aacute;o Vi&ecirc;n &hellip;</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Nếu số tiền vay tr&ecirc;n 10 triệu, người mua phải chứng minh thu nhập (giấy tờ rất đơn giản)</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Tất cả giấy tờ l&agrave; bản photo kh&ocirc;ng cần c&ocirc;ng chứng.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- H&oacute;a đơn x&aacute;c nhận địa chỉ nơi ở của người mua h&agrave;ng: h&oacute;a đơn tiền điện, h&oacute;a đơn tiền nước, h&oacute;a đơn tiền internet, h&oacute;a đơn truyền h&igrave;nh c&aacute;p, h&oacute;a đơn điện thoại di động trả sau, giấy tạm tr&uacute; &hellip;</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel"><strong>ĐỐI VỚI KH&Aacute;CH H&Agrave;NG L&Agrave; CHỦ HỘ KINH DOANH:</strong></span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- L&agrave; c&ocirc;ng d&acirc;n Việt Nam đang kinh doanh tại H&agrave; Nội dưới h&igrave;nh thức cửa h&agrave;ng hoặc c&ocirc;ng ty.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Từ 21 &ndash; 60 tuổi.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Thu nhập từ 1,500,000 vnd trở l&ecirc;n.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel"><strong>Những giấy tờ cần chuẩn bị:</strong></span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- CMND photo.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Sổ hộ khẩu nguy&ecirc;n cuấn photo.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- H&oacute;a đơn x&aacute;c nhận địa chỉ nơi ở của người mua h&agrave;ng.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Giấy ph&eacute;p đăng k&yacute; kinh doanh photo.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel"><strong><span style="text-decoration: underline;">Ch&uacute; &yacute;:</span></strong></span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Chủ hộ kinh doanh c&oacute; hộ khẩu ở H&agrave; Nội hoặc Ngoại tỉnh v&agrave; đang kinh doanh tại H&agrave; Nội cũng được hỗ trợ.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Nếu số tiền vay tr&ecirc;n 10 triệu, người mua phải chứng minh thu nhập (giấy tờ rất đơn giản).</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Tất cả giấy tờ l&agrave; bản photo kh&ocirc;ng cần c&ocirc;ng chứng.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- H&oacute;a đơn x&aacute;c nhận địa chỉ nơi ở của người mua h&agrave;ng: h&oacute;a đơn tiền điện, h&oacute;a đơn tiền nước, h&oacute;a đơn tiền internet, h&oacute;a đơn truyền h&igrave;nh c&aacute;p, h&oacute;a đơn điện thoại di động trả sau, giấy tạm tr&uacute; &hellip;</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel"><strong>ĐĂNG K&Yacute; ONLINE.</strong></span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Kh&aacute;ch h&agrave;ng c&oacute; thể tải Form đăng k&yacute; tr&ecirc;n website của HanoiComputer</span></p>\r\n\n<ul>\r\n\n	<li>\r\n\n		<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">Mẫu d&agrave;nh cho chủ hộ kinh doanh - <a href="http://www.hanoicomputer.vn/tragop/hnc-form-hokinhdoanh.doc">Download</a></span></li>\r\n\n	<li>\r\n\n		<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">Mẫu d&agrave;nh cho người đi l&agrave;m&nbsp;- <a href="http://www.hanoicomputer.vn/tragop/hnc-form-nguoidilam.doc">Download</a></span></li>\r\n\n	<li>\r\n\n		<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">Mẫu d&agrave;nh cho sinh vi&ecirc;n&nbsp;- <a href="http://www.hanoicomputer.vn/tragop/hnc-form-sinhvien.doc">Download</a></span></li>\r\n\n</ul>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Điền đủ th&ocirc;ng tin v&agrave;o Form v&agrave; gửi v&agrave;o h&ograve;m thư:&nbsp;<strong>tragop@hanoicomputer.com</strong></span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Kết quả sẽ được trả lời từ 30 ph&uacute;t đến 02 ng&agrave;y (trừ thứ 7 v&agrave; chủ nhật).</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Nếu kh&ocirc;ng nhận được kết quả c&oacute; thể gọi số sau để hỏi về t&igrave;nh trạng hồ sơ: </span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel"><strong>Ms Hồng Nhung: 0988.069.886 hoặc 04.35380088 m&aacute;y lẻ 11 (43 Th&aacute;i H&agrave;)</strong></span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel"><strong>Mr Trần Duy Thanh:&nbsp;0915559267 (131 L&ecirc; Thanh Nghị)</strong></span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel"><strong>Chuẩn bị hồ sơ.</strong></span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Ngay khi HanoiComputer nhận được Form đăng k&yacute;, hồ sơ sẽ được thẩm định.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Kết quả của hồ sơ sẽ được th&ocirc;ng b&aacute;o trực tiếp cho kh&aacute;ch h&agrave;ng bằng điện thoại.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Kh&aacute;ch h&agrave;ng n&ecirc;n ghi lại những giấy tờ HanoiComputer&nbsp;th&ocirc;ng b&aacute;o v&agrave; mang đủ đến cho tư vấn vi&ecirc;n của HanoiComputer</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Kh&aacute;ch h&agrave;ng n&ecirc;n li&ecirc;n lạc với tư vấn vi&ecirc;n tại cửa h&agrave;ng trước khi đến nhận b&agrave;n giao sản phẩm: </span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel"><strong>Ms Hồng Nhung: 0988.069.886 hoặc 04.35380088 m&aacute;y lẻ 11 (43 Th&aacute;i H&agrave;)</strong></span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel"><strong><span style="font-weight: normal; "><strong>Mr Trần Duy Thanh:&nbsp;0915559267 (131 L&ecirc; Thanh Nghị)</strong></span></strong></span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- Trong v&ograve;ng 30 &ndash; 45 ph&uacute;t, nh&acirc;n vi&ecirc;n tư vấn sẽ gi&uacute;p kh&aacute;ch h&agrave;ng ho&agrave;n th&agrave;nh thủ tục mua h&agrave;ng trả g&oacute;p.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel"><strong>ĐĂNG K&Yacute; TẠI CỬA H&Agrave;NG.</strong></span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">Kh&aacute;ch h&agrave;ng c&oacute; thể đến c&aacute;c showroom của HanoiComputer để đăng k&yacute; mua h&agrave;ng trả g&oacute;p tại c&aacute;c địa chỉ sau:</span></p>\r\n\n<ul>\r\n\n	<li>\r\n\n		<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">HanoiComputer<strong> &ndash; 131 L&ecirc; Thanh Nghị - Hai B&agrave; Trưng - H&agrave; Nội</strong></span></li>\r\n\n	<li>\r\n\n		<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">HanoiComputer<strong style="font-weight: bold;">&nbsp;&ndash; 43 Th&aacute;i H&agrave; - Đống Đa - H&agrave; Nội</strong></span></li>\r\n\n</ul>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">Kh&aacute;ch h&agrave;ng h&atilde;y li&ecirc;n hệ với&nbsp;</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel"><strong>Ms Hồng Nhung: 0988.069.886 hoặc 04.35380088 m&aacute;y lẻ 11 (43 Th&aacute;i H&agrave;)</strong></span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel"><strong><span style="font-weight: normal; "><strong>Mr Trần Duy Thanh:&nbsp;0915559267 (131 L&ecirc; Thanh Nghị)</strong></span></strong></span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel"><strong>Email: tragop@hanoicomputer.com</strong></span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">để được giải đ&aacute;p về th&ocirc;ng tin trả g&oacute;p.</span></p>\r\n\n', 2, 'upload/news/resize_108_heo_dat00557058.jpg', 'upload/news/resize_108_heo_dat00557058_thumb.jpg', 0),
(6, 'Hội thảo: AMD – Công nghệ hướng tới tương lai', '<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">Thời gian: 14h30 &ndash; 16h30 Thứ 6 &ndash; Ng&agrave;y 10/12/2010 Địa điểm: Conference Hall &ndash; 4th Floor Aptech 356 Giải Ph&oacute;ng Diễn giả: Đến từ HanoiComputer</span></p>\r\n\n', '<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">Đăng k&yacute; trực tuyến tại: <a href="http://www.aiti-aptech.edu.vn/index.php/hoi-thao">http://www.aiti-aptech.edu.vn/index.php/hoi-thao</a></span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel"><strong>Tổng quan nội dung hội thảo:</strong></span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">1. Giới thiệu về cơ bản bộ vi xử l&yacute; AMD<br />\r\n\n	2. Hỏi đ&aacute;p c&aacute;c vấn đề xung quanh bộ vi xử l&yacute; AMD giữa sinh vi&ecirc;n<br />\r\n\n	3. Giới thiệu những hệ thống sử dụng bộ vi xử l&yacute; AMD của c&ocirc;ng ty m&aacute;y t&iacute;nh H&agrave; Nội<br />\r\n\n	4. Show demo c&aacute;c sản phẩm hỗ trợ c&aacute;c coder<br />\r\n\n	5. Nghe hỏi đ&aacute;p từ ph&iacute;a sinh vi&ecirc;n.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">NOTE: C&oacute; phần qu&agrave; cho phần Q&amp;A l&agrave; c&aacute;c sản phẩm của được giảm g&iacute;a đến h&agrave;ng chục %.<br />\r\n\n	H&atilde;y tham gia v&agrave; t&igrave;m hiểu về ch&acirc;n trời c&ocirc;ng nghệ mới !</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">﻿<img alt="" height="799" src="http://www.aiti-aptech.edu.vn/wp-content/uploads/2010/12/A4_AMD_web.jpg" style="display: block; margin-left: auto; margin-right: auto;" width="565" /></span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">C&ocirc;ng việc của một lập tr&igrave;nh vi&ecirc;n trước đ&acirc;y tập trung mạnh v&agrave;o việc coding, ngồi t&igrave;m lỗi, hay t&igrave;m những &yacute; tưởng tạo n&ecirc;n một phần mềm tuyệt vời. Thế nhưng việc đ&oacute; cần rất&nbsp;nhiều thời gian,v&agrave; t&oacute;c tai của bạn sẽ rối b&ugrave; nếu gần đến ng&agrave;y nộp đề t&agrave;i m&agrave; vẫn rất b&iacute;.&nbsp;L&yacute; do ch&iacute;nh một phần do cảm hứng của bạn chưa l&ecirc;n cao,do bạn đ&atilde; qu&aacute; gượng &eacute;p cố&nbsp;nghĩ một điều g&igrave; đ&oacute; kh&aacute;c biệt, v&agrave; cuối c&ugrave;ng l&agrave; chẳng may đang chạy th&igrave; m&aacute;y lỗi,khiến bạn kh&oacute; chịu,bực m&igrave;nh v&agrave; ko muốn l&agrave;m nữa.</span></p>\r\n\n<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">V&agrave; giờ đến l&uacute;c bạn cần thay đổi: thay đổi c&aacute;ch nghĩ,thay đổi phong c&aacute;ch v&agrave; thay đổi một chiếc &aacute;o cho người bạn đời của bạn. <strong>Đ&oacute; l&agrave; bộ m&aacute;y t&iacute;nh</strong>!&nbsp;Bộ m&aacute;y t&iacute;nh ng&agrave;y xưa ch&uacute;ng ta cần một hệ thống nồi đồng cối đ&aacute;, chạy l&acirc;u, bền. Thế nhưng c&ocirc;ng việc ấy tưởng chừng như đơn giản m&agrave; ko hề dễ ch&uacute;t n&agrave;o, bạn cần c&oacute; một bộ nguồn ổn định c&ocirc;ng suất thực, bạn cần c&oacute; một vỏ case chắc chắn đảm bảo những chiếc ổ cứng của bạn ko bị va đập mạnh khiến hỏng h&oacute;c&hellip;.v&agrave; b&acirc;y giờ để ch&uacute;ng ta c&oacute; cảm hứng l&agrave;m nhiều việc tốt hơn nữa, ch&uacute;ng ta cần những khoảng thời gian giải tr&iacute;: một card h&igrave;nh để chơi game &ndash; xem phim, một bộ vỏ m&aacute;y t&iacute;nh thật l&agrave; sặc sỡ để mỗi khi ta b&iacute; &yacute; tưởng nh&igrave;n v&agrave;o lại thấy tr&agrave;n trề sinh lực để l&agrave;m ra những c&aacute;i mới&hellip; hay một bộ vi xử l&yacute; kh&aacute;c biệt cho việc hỗ trợ t&iacute;nh to&aacute;n tập code lập tr&igrave;nh của bạn. Tất cả sẽ n&oacute;i trong&nbsp;buổi hội thảo tại trường AiTi Aptech: H&agrave; Nội Computer v&agrave; AMD &ndash; c&ocirc;ng nghệ hướng tới tương lai.</span></p>\r\n\n', 2, 'upload/news/resize_52_logof.jpg', 'upload/news/resize_52_logof_thumb.jpg', 1),
(7, 'Chính sách mới: Giao hàng miễn phí trong bán kính 100km', '<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">Kể từ ng&agrave;y 10/11/2010, C&ocirc;ng ty M&aacute;y T&iacute;nh H&agrave; Nội giao h&agrave;ng miễn ph&iacute; trong phạm vi b&aacute;n k&iacute;nh 100km cho c&aacute;c đối tượng kh&aacute;ch h&agrave;ng: </span></p>\r\n\n', '<p>\r\n\n	<span id="ctl00_HomeMasterBodyHolder_ucNewsList_contentLabel">- C&aacute;c kh&aacute;ch h&agrave;ng kinh doanh dịch vụ Game, Internet mua m&aacute;y t&iacute;nh với số lượng 10 chiếc trở l&ecirc;n (bao gồm cả m&agrave;n h&igrave;nh).<br />\r\n\n	- C&aacute;c kh&aacute;ch h&agrave;ng l&agrave; doanh nghiệp v&agrave; c&aacute;c tổ chức mua h&agrave;ng từ 5 bộ m&aacute;y t&iacute;nh trở l&ecirc;n.<br />\r\n\n	Ngo&agrave;i ra, đối với kh&aacute;ch h&agrave;ng mua m&aacute;y t&iacute;nh v&agrave; linh kiện lẻ, Hanoicomputer vẫn sẽ giao h&agrave;ng miễn ph&iacute; tận nơi trong TP H&agrave; Nội (b&aacute;n k&iacute;nh 20 KM t&iacute;nh từ địa điểm b&aacute;n h&agrave;ng của C&ocirc;ng ty).</span></p>\r\n\n', 2, 'upload/news/resize_22_mien-phi-van-chuyen.jpg', 'upload/news/resize_22_mien-phi-van-chuyen_thumb.jpg', 0);

