CREATE TABLE IF NOT EXISTS `product` (
  `p_id` int(11) NOT NULL AUTO_INCREMENT,
  `p_code` int(5) NOT NULL,
  `p_name` text CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `p_price` int(10) NOT NULL,
  `p_info` text CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `p_thongso` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `p_khuyenmai` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `p_image` text CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `p_image_thumb` text CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `p_numview` int(5) DEFAULT NULL,
  `active_index` tinyint(4) DEFAULT NULL,
  `active_top` tinyint(4) DEFAULT NULL,
  `active_slishow` tinyint(4) NOT NULL,
  `p_cate_id` int(5) NOT NULL,
  `active` tinyint(4) NOT NULL,
  PRIMARY KEY (`p_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=41 ;

